// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.0;

contract PythStructs {
    // A price with a degree of uncertainty, represented as a price +- a confidence interval.
    //
    // The confidence interval roughly corresponds to the standard error of a normal distribution.
    // Both the price and confidence are stored in a fixed-point numeric representation,
    // `x * (10^expo)`, where `expo` is the exponent.
    //
    // Please refer to the documentation at https://docs.pyth.network/documentation/pythnet-price-feeds/best-practices for how
    // to how this price safely.
    struct Price {
        // Price
        int64 price;
        // Confidence interval around the price
        uint64 conf;
        // Price exponent
        int32 expo;
        // Unix timestamp describing when the price was published
        uint publishTime;
    }

    // PriceFeed represents a current aggregate price from pyth publisher feeds.
    struct PriceFeed {
        // The price ID.
        bytes32 id;
        // Latest available price
        Price price;
        // Latest available exponentially-weighted moving average price
        Price emaPrice;
    }

    struct TwapPriceFeed {
        // The price ID.
        bytes32 id;
        // Start time of the TWAP
        uint64 startTime;
        // End time of the TWAP
        uint64 endTime;
        // TWAP price
        Price twap;
        // Down slot ratio represents the ratio of price feed updates that were missed or unavailable
        // during the TWAP period, expressed as a fixed-point number between 0 and 1e6 (100%).
        // For example:
        //   - 0 means all price updates were available
        //   - 500_000 means 50% of updates were missed
        //   - 1_000_000 means all updates were missed
        // This can be used to assess the quality/reliability of the TWAP calculation.
        // Applications should define a maximum acceptable ratio (e.g. 100000 for 10%)
        // and revert if downSlotsRatio exceeds it.
        uint32 downSlotsRatio;
    }

    // Information used to calculate time-weighted average prices (TWAP)
    struct TwapPriceInfo {
        // slot 1
        int128 cumulativePrice;
        uint128 cumulativeConf;
        // slot 2
        uint64 numDownSlots;
        uint64 publishSlot;
        uint64 publishTime;
        uint64 prevPublishTime;
        // slot 3
        int32 expo;
    }
}

contracts/OracleAggregator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@pythnetwork/pyth-sdk-solidity/IPyth.sol";
import "@pythnetwork/pyth-sdk-solidity/PythStructs.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract OracleAggregator is Ownable {
    IPyth public pyth;

    // 股票符号 => Pyth Feed ID 映射
    mapping(string => bytes32) public symbolToFeedId;

    // 已支持的股票符号列表
    string[] public supportedSymbols;

    // 事件
    event FeedIdUpdated(string indexed symbol, bytes32 feedId);
    event FeedIdRemoved(string indexed symbol);

    constructor(address pythContract) Ownable(msg.sender) {
        pyth = IPyth(pythContract);
    }

    // 设置股票符号对应的 Feed ID
    function setFeedId(string memory symbol, bytes32 feedId) external onlyOwner {
        require(bytes(symbol).length > 0, "Symbol cannot be empty");
        require(feedId != bytes32(0), "Feed ID cannot be zero");

        // 如果是新符号，添加到支持列表
        if (symbolToFeedId[symbol] == bytes32(0)) {
            supportedSymbols.push(symbol);
        }

        symbolToFeedId[symbol] = feedId;
        emit FeedIdUpdated(symbol, feedId);
    }

    // 移除股票符号
    function removeFeedId(string memory symbol) external onlyOwner {
        require(symbolToFeedId[symbol] != bytes32(0), "Symbol not found");

        symbolToFeedId[symbol] = bytes32(0);

        // 从支持列表中移除
        for (uint i = 0; i < supportedSymbols.length; i++) {
            if (keccak256(bytes(supportedSymbols[i])) == keccak256(bytes(symbol))) {
                supportedSymbols[i] = supportedSymbols[supportedSymbols.length - 1];
                supportedSymbols.pop();
                break;
            }
        }

        emit FeedIdRemoved(symbol);
    }

    /// @notice 根据股票符号查询价格（仅查询版本）
    /// @dev 只查询已缓存的价格，不会更新。推荐使用 updateAndGetPrices 获取最新价格
    /// @dev 警告：此函数返回的可能不是最新价格，仅用于查询已缓存的价格数据
    function getPrice(string memory symbol) external view returns (
        uint256 price, 
        uint256 minPrice, 
        uint256 maxPrice, 
        uint256 publishTime
    ) {
        bytes32 feedId = symbolToFeedId[symbol];
        require(feedId != bytes32(0), "Price feed not found for symbol");

        // 获取缓存的价格数据（可能不是最新的）
        // 要获取最新价格，请使用 updateAndGetPrices 函数
        PythStructs.Price memory p = pyth.getPriceUnsafe(feedId);
        require(p.price > 0, "Invalid price data");

        // 动态转换为 18 位小数精度
        // Pyth价格格式：price * 10^expo = 实际价格
        // 我们需要转换为：实际价格 * 10^18

        uint256 absPrice = uint256(uint64(p.price)); // 确保为正数

        if (p.expo >= 0) {
            // expo >= 0: price已经是整数，需要乘以10^expo，然后再乘以10^18
            price = absPrice * (10 ** uint256(int256(p.expo))) * 1e18;
        } else {
            // expo < 0: price需要除以10^(-expo)来得到实际价格，然后乘以10^18
            // 为了避免精度丢失，我们重新排列计算顺序
            int256 negExpo = -int256(p.expo);
            if (negExpo >= 18) {
                // 如果负指数大于等于18，结果会很小
                price = absPrice * 1e18 / (10 ** uint256(negExpo));
            } else {
                // 如果负指数小于18，我们可以优化计算避免精度丢失
                uint256 adjustment = 18 - uint256(negExpo);
                price = absPrice * (10 ** adjustment);
            }
        }

        // 简单设置最小最大价格（可以根据需要调整）
        minPrice = price * 95 / 100; // -5%
        maxPrice = price * 105 / 100; // +5%
        publishTime = p.publishTime;
    }

    /// @notice 更新并获取单个股票的最新价格
    /// @param symbol 股票符号
    /// @param updateData 价格更新数据
    /// @return price 转换为18位小数的价格
    /// @return minPrice 最小价格（-5%）
    /// @return maxPrice 最大价格（+5%）
    /// @return publishTime 发布时间
    function updateAndGetPrice(
        string memory symbol,
        bytes[] calldata updateData
    ) external payable returns (
        uint256 price,
        uint256 minPrice,
        uint256 maxPrice,
        uint256 publishTime
    ) {
        // 1. 更新链上价格
        uint fee = pyth.getUpdateFee(updateData);
        require(msg.value >= fee, "Insufficient fee");
        pyth.updatePriceFeeds{value: fee}(updateData);

        // 2. 获取最新价格
        bytes32 feedId = symbolToFeedId[symbol];
        require(feedId != bytes32(0), "Price feed not found for symbol");

        PythStructs.Price memory p = pyth.getPriceUnsafe(feedId);
        require(p.price > 0, "Invalid price data");

        // 动态转换为 18 位小数精度
        uint256 absPrice = uint256(uint64(p.price)); // 确保为正数

        if (p.expo >= 0) {
            // expo >= 0: price已经是整数，需要乘以10^expo，然后再乘以10^18
            price = absPrice * (10 ** uint256(int256(p.expo))) * 1e18;
        } else {
            // expo < 0: price需要除以10^(-expo)来得到实际价格，然后乘以10^18
            // 为了避免精度丢失，我们重新排列计算顺序
            int256 negExpo = -int256(p.expo);
            if (negExpo >= 18) {
                // 如果负指数大于等于18，结果会很小
                price = absPrice * 1e18 / (10 ** uint256(negExpo));
            } else {
                // 如果负指数小于18，我们可以优化计算避免精度丢失
                uint256 adjustment = 18 - uint256(negExpo);
                price = absPrice * (10 ** adjustment);
            }
        }

        // 简单设置最小最大价格（可以根据需要调整）
        minPrice = price * 95 / 100; // -5%
        maxPrice = price * 105 / 100; // +5%
        publishTime = p.publishTime;

        // 返还多余的费用
        if (msg.value > fee) {
            payable(msg.sender).transfer(msg.value - fee);
        }
    }

    /// @notice 更新价格数据（在查询价格前调用）
    /// @param updateData 从 Pyth 网络获取的价格更新数据
    function updatePriceFeeds(bytes[] calldata updateData) external payable {
        // 计算更新费用
        uint fee = pyth.getUpdateFee(updateData);
        require(msg.value >= fee, "Insufficient fee");

        // 更新链上价格
        pyth.updatePriceFeeds{value: fee}(updateData);

        // 返还多余的费用
        if (msg.value > fee) {
            payable(msg.sender).transfer(msg.value - fee);
        }
    }

    /// @notice 获取更新价格所需的费用
    function getUpdateFee(bytes[] calldata updateData) external view returns (uint256) {
        return pyth.getUpdateFee(updateData);
    }

    /// @notice 批量更新并查询价格（推荐使用）
    /// @param symbols 要查询的股票符号数组
    /// @param updateData 价格更新数据
    function updateAndGetPrices(
        string[] memory symbols, 
        bytes[] calldata updateData
    ) external payable returns (
        uint256[] memory prices,
        uint256[] memory publishTimes
    ) {
        // 1. 更新链上价格
        uint fee = pyth.getUpdateFee(updateData);
        require(msg.value >= fee, "Insufficient fee");
        pyth.updatePriceFeeds{value: fee}(updateData);

        // 2. 查询价格
        prices = new uint256[](symbols.length);
        publishTimes = new uint256[](symbols.length);

        for (uint i = 0; i < symbols.length; i++) {
            bytes32 feedId = symbolToFeedId[symbols[i]];
            require(feedId != bytes32(0), "Price feed not found for symbol");

            PythStructs.Price memory p = pyth.getPriceUnsafe(feedId);
            require(p.price > 0, "Invalid price data");

            // 动态转换为 18 位小数精度（与getPrice函数保持一致）
            uint256 absPrice = uint256(uint64(p.price)); // 确保为正数

            if (p.expo >= 0) {
                // expo >= 0: price已经是整数，需要乘以10^expo，然后再乘以10^18
                prices[i] = absPrice * (10 ** uint256(int256(p.expo))) * 1e18;
            } else {
                // expo < 0: price需要除以10^(-expo)来得到实际价格，然后乘以10^18
                // 为了避免精度丢失，我们重新排列计算顺序
                int256 negExpo = -int256(p.expo);
                if (negExpo >= 18) {
                    // 如果负指数大于等于18，结果会很小
                    prices[i] = absPrice * 1e18 / (10 ** uint256(negExpo));
                } else {
                    // 如果负指数小于18，我们可以优化计算避免精度丢失
                    uint256 adjustment = 18 - uint256(negExpo);
                    prices[i] = absPrice * (10 ** adjustment);
                }
            }

            publishTimes[i] = p.publishTime;
        }

        // 返还多余的费用
        if (msg.value > fee) {
            payable(msg.sender).transfer(msg.value - fee);
        }
    }

    /// @notice 获取所有支持的股票符号
    function getSupportedSymbols() external view returns (string[] memory) {
        return supportedSymbols;
    }

    /// @notice 检查是否支持某个股票符号
    function isSymbolSupported(string memory symbol) external view returns (bool) {
        return symbolToFeedId[symbol] != bytes32(0);
    }

    /// @notice 批量设置 Feed ID
    function batchSetFeedIds(string[] memory symbols, bytes32[] memory feedIds) external onlyOwner {
        require(symbols.length == feedIds.length, "Arrays length mismatch");

        for (uint i = 0; i < symbols.length; i++) {
            require(bytes(symbols[i]).length > 0, "Symbol cannot be empty");
            require(feedIds[i] != bytes32(0), "Feed ID cannot be zero");

            // 如果是新符号，添加到支持列表
            if (symbolToFeedId[symbols[i]] == bytes32(0)) {
                supportedSymbols.push(symbols[i]);
            }

            symbolToFeedId[symbols[i]] = feedIds[i];
        }
    }
}

contracts/StockToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./OracleAggregator.sol";

/**
 * @title StockToken - 改进版股票代币合约
 * @dev 可升级的ERC20代币，代表股票份额，支持买卖功能
 * 
 * 主要改进：
 * 1. 代币初始化时分配给owner，而非合约
 * 2. 修复价格计算逻辑
 * 3. 添加安全机制（暂停、重入保护）
 * 4. 增加管理功能（提取、调整参数）
 * 5. 添加滑点保护和最小交易限制
 */
contract StockToken is
    Initializable,
    ERC20Upgradeable,
    OwnableUpgradeable,
    UUPSUpgradeable,
    PausableUpgradeable,
    ReentrancyGuardUpgradeable
{
    OracleAggregator public oracleAggregator;
    IERC20 public usdtToken;
    string public stockSymbol;

    // 交易参数
    uint256 public minTradeAmount = 1e6; // 最小交易金额 1 USDT (6 decimals)
    uint256 public maxSlippage = 300; // 最大滑点 3% (基点表示)
    uint256 public tradeFeeRate = 30; // 交易手续费 0.3% (基点表示)
    address public feeReceiver; // 手续费接收地址

    // 事件
    event TokenPurchased(address indexed buyer, uint256 usdtAmount, uint256 tokenAmount, uint256 price);
    event TokenSold(address indexed seller, uint256 tokenAmount, uint256 usdtAmount, uint256 price);
    event ParameterUpdated(string indexed parameter, uint256 oldValue, uint256 newValue);
    event EmergencyWithdraw(address indexed token, address indexed to, uint256 amount);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        string memory name_,
        string memory symbol_,
        uint256 initialSupply_,
        address owner_,
        address oracleAggregator_,
        address usdtToken_
    ) public initializer {
        __ERC20_init(name_, symbol_);
        __Ownable_init(owner_);
        __UUPSUpgradeable_init();
        __Pausable_init();
        __ReentrancyGuard_init();

        stockSymbol = symbol_;
        oracleAggregator = OracleAggregator(oracleAggregator_);
        usdtToken = IERC20(usdtToken_);
        feeReceiver = owner_; // 默认手续费接收者为owner

        // 🔥 关键改进：代币分配给owner而不是合约
        _mint(owner_, initialSupply_);
    }

    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyOwner {}

    /**
     * @dev 铸造新代币 - 分配给owner
     */
    function mint(uint256 amount) external onlyOwner {
        _mint(owner(), amount);
    }

    /**
     * @dev 向合约注入代币用于交易
     */
    function injectTokens(uint256 amount) external onlyOwner {
        require(balanceOf(owner()) >= amount, "Insufficient owner balance");
        _transfer(owner(), address(this), amount);
    }

    /**
     * @dev 购买股票代币
     * @param usdtAmount 投入的USDT数量
     * @param minTokenAmount 最少获得的代币数量（滑点保护）
     * @param updateData 价格更新数据
     */
    function buy(uint256 usdtAmount, uint256 minTokenAmount, bytes[] calldata updateData) 
        external 
        payable
        nonReentrant 
        whenNotPaused 
    {
        require(usdtAmount >= minTradeAmount, "Amount below minimum");

        // 更新并获取最新股票价格
        (uint256 stockPrice, , , ) = oracleAggregator.updateAndGetPrice{value: msg.value}(stockSymbol, updateData);
        require(stockPrice > 0, "Invalid stock price");

        // 🔥 修复价格计算逻辑
        // USDT: 6 decimals, stockPrice: 18 decimals, 目标: 18 decimals
        // tokenAmount = (usdtAmount * 1e12) * 1e18 / stockPrice
        uint256 tokenAmountBeforeFee = (usdtAmount * 1e30) / stockPrice;

        // 计算手续费
        uint256 feeAmount = (tokenAmountBeforeFee * tradeFeeRate) / 10000;
        uint256 tokenAmount = tokenAmountBeforeFee - feeAmount;

        // 滑点保护
        require(tokenAmount >= minTokenAmount, "Slippage too high");

        // 检查合约代币余额
        require(
            balanceOf(address(this)) >= tokenAmount,
            "Insufficient token supply"
        );

        // 转移USDT到合约
        require(
            usdtToken.transferFrom(msg.sender, address(this), usdtAmount),
            "USDT transfer failed"
        );

        // 转移代币给用户
        _transfer(address(this), msg.sender, tokenAmount);

        // 转移手续费代币给手续费接收者
        if (feeAmount > 0) {
            _transfer(address(this), feeReceiver, feeAmount);
        }

        emit TokenPurchased(msg.sender, usdtAmount, tokenAmount, stockPrice);
    }

    /**
     * @dev 出售股票代币
     * @param tokenAmount 出售的代币数量
     * @param minUsdtAmount 最少获得的USDT数量（滑点保护）
     * @param updateData 价格更新数据
     */
    function sell(uint256 tokenAmount, uint256 minUsdtAmount, bytes[] calldata updateData) 
        external 
        payable
        nonReentrant 
        whenNotPaused 
    {
        require(tokenAmount > 0, "Invalid token amount");
        require(
            balanceOf(msg.sender) >= tokenAmount,
            "Insufficient token balance"
        );

        // 更新并获取最新股票价格
        (uint256 stockPrice, , , ) = oracleAggregator.updateAndGetPrice{value: msg.value}(stockSymbol, updateData);
        require(stockPrice > 0, "Invalid stock price");

        // 🔥 修复价格计算逻辑
        // tokenAmount: 18 decimals, stockPrice: 18 decimals, 目标: 6 decimals (USDT)
        uint256 usdtAmountBeforeFee = (tokenAmount * stockPrice) / 1e30;

        // 计算手续费
        uint256 feeAmount = (usdtAmountBeforeFee * tradeFeeRate) / 10000;
        uint256 usdtAmount = usdtAmountBeforeFee - feeAmount;

        // 滑点保护
        require(usdtAmount >= minUsdtAmount, "Slippage too high");
        require(usdtAmount >= minTradeAmount, "Amount below minimum");

        // 检查合约USDT余额
        require(
            usdtToken.balanceOf(address(this)) >= usdtAmount + feeAmount,
            "Insufficient USDT in contract"
        );

        // 转移代币到合约
        _transfer(msg.sender, address(this), tokenAmount);

        // 转移USDT给用户
        require(
            usdtToken.transfer(msg.sender, usdtAmount),
            "USDT transfer failed"
        );

        // 转移手续费USDT给手续费接收者
        if (feeAmount > 0) {
            require(
                usdtToken.transfer(feeReceiver, feeAmount),
                "Fee transfer failed"
            );
        }

        emit TokenSold(msg.sender, tokenAmount, usdtAmount, stockPrice);
    }

    /**
     * @dev 获取购买预估（包含手续费计算）
     * @notice 此函数使用缓存价格，可能不是最新价格。建议在实际交易前先调用buy函数获取最新价格。
     */
    function getBuyEstimate(uint256 usdtAmount) 
        external 
        view 
        returns (uint256 tokenAmount, uint256 feeAmount) 
    {
        (uint256 stockPrice, , , ) = oracleAggregator.getPrice(stockSymbol);
        require(stockPrice > 0, "Invalid stock price");

        uint256 tokenAmountBeforeFee = (usdtAmount * 1e30) / stockPrice;
        feeAmount = (tokenAmountBeforeFee * tradeFeeRate) / 10000;
        tokenAmount = tokenAmountBeforeFee - feeAmount;
    }

    /**
     * @dev 获取出售预估（包含手续费计算）
     * @notice 此函数使用缓存价格，可能不是最新价格。建议在实际交易前先调用sell函数获取最新价格。
     */
    function getSellEstimate(uint256 tokenAmount) 
        external 
        view 
        returns (uint256 usdtAmount, uint256 feeAmount) 
    {
        (uint256 stockPrice, , , ) = oracleAggregator.getPrice(stockSymbol);
        require(stockPrice > 0, "Invalid stock price");

        uint256 usdtAmountBeforeFee = (tokenAmount * stockPrice) / 1e30;
        feeAmount = (usdtAmountBeforeFee * tradeFeeRate) / 10000;
        usdtAmount = usdtAmountBeforeFee - feeAmount;
    }

    // ========== 管理功能 ==========

    /**
     * @dev 设置交易参数
     */
    function setTradeParameters(
        uint256 _minTradeAmount,
        uint256 _maxSlippage,
        uint256 _tradeFeeRate
    ) external onlyOwner {
        require(_maxSlippage <= 1000, "Max slippage too high"); // 最大10%
        require(_tradeFeeRate <= 1000, "Trade fee too high"); // 最大10%

        emit ParameterUpdated("minTradeAmount", minTradeAmount, _minTradeAmount);
        emit ParameterUpdated("maxSlippage", maxSlippage, _maxSlippage);
        emit ParameterUpdated("tradeFeeRate", tradeFeeRate, _tradeFeeRate);

        minTradeAmount = _minTradeAmount;
        maxSlippage = _maxSlippage;
        tradeFeeRate = _tradeFeeRate;
    }

    /**
     * @dev 设置手续费接收地址
     */
    function setFeeReceiver(address _feeReceiver) external onlyOwner {
        require(_feeReceiver != address(0), "Invalid fee receiver");
        feeReceiver = _feeReceiver;
    }

    /**
     * @dev 暂停/恢复合约
     */
    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev 紧急提取功能
     */
    function emergencyWithdrawToken(uint256 amount) external onlyOwner {
        require(amount <= balanceOf(address(this)), "Insufficient balance");
        _transfer(address(this), owner(), amount);
        emit EmergencyWithdraw(address(this), owner(), amount);
    }

    function emergencyWithdrawUSDT(uint256 amount) external onlyOwner {
        require(amount <= usdtToken.balanceOf(address(this)), "Insufficient balance");
        require(usdtToken.transfer(owner(), amount), "Transfer failed");
        emit EmergencyWithdraw(address(usdtToken), owner(), amount);
    }

    // ========== 查询功能 ==========

    /**
     * @dev 获取股票价格
     * @notice 此函数返回缓存价格，可能不是最新价格。要获取最新价格，请使用updateAndGetStockPrice函数。
     */
    function getStockPrice() external view returns (uint256) {
        (uint256 price, , , ) = oracleAggregator.getPrice(stockSymbol);
        return price;
    }

    /**
     * @dev 更新并获取最新股票价格
     * @param updateData 价格更新数据
     * @return price 最新股票价格
     * @return minPrice 最小价格
     * @return maxPrice 最大价格
     * @return publishTime 发布时间
     */
    function updateAndGetStockPrice(bytes[] calldata updateData) 
        external 
        payable 
        returns (uint256 price, uint256 minPrice, uint256 maxPrice, uint256 publishTime) 
    {
        return oracleAggregator.updateAndGetPrice{value: msg.value}(stockSymbol, updateData);
    }

    function getContractTokenBalance() external view returns (uint256) {
        return balanceOf(address(this));
    }

    function getContractUSDTBalance() external view returns (uint256) {
        return usdtToken.balanceOf(address(this));
    }

    function getTradingInfo() external view returns (
        uint256 _minTradeAmount,
        uint256 _maxSlippage,
        uint256 _tradeFeeRate,
        address _feeReceiver,
        bool _paused
    ) {
        return (minTradeAmount, maxSlippage, tradeFeeRate, feeReceiver, paused());
    }
}

contracts/TokenFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";
import "./StockToken.sol";

// ===================== 代币工厂合约 (UUPS 可升级) =====================
contract TokenFactory is Initializable, UUPSUpgradeable, OwnableUpgradeable {
    // 代币符号 => 代币合约地址
    mapping(string => address) public tokenBySymbol;

    // 所有代币地址列表
    address[] public allTokens;

    // 预言机聚合器地址
    address public oracleAggregator;

    // StockToken 实现合约地址
    address public stockTokenImplementation;

    // USDT 代币地址
    address public usdtTokenAddress;

    event TokenCreated(address indexed tokenAddress, string name, string symbol);
    event OracleUpdated(address newOracle);
    event ImplementationUpdated(address newImplementation);


    function initialize(address _oracleAggregator, address _stockTokenImplementation, address _usdtTokenAddress) initializer public {
        __Ownable_init(msg.sender);
        __UUPSUpgradeable_init();
        oracleAggregator = _oracleAggregator;
        stockTokenImplementation = _stockTokenImplementation;
        usdtTokenAddress = _usdtTokenAddress;
    }

    function _authorizeUpgrade(address) internal override onlyOwner {}

    // 创建新代币
    function createToken(
        string memory name,
        string memory symbol,
        uint256 initialSupply
    ) external onlyOwner returns (address) {
        require(bytes(name).length > 0, "Token name cannot be empty");
        require(bytes(symbol).length > 0, "Token symbol cannot be empty");
        require(initialSupply > 0, "Initial supply must be greater than 0");
        require(tokenBySymbol[symbol] == address(0), "Token already exists");
        require(stockTokenImplementation != address(0), "Implementation not set");
        require(usdtTokenAddress != address(0), "USDT token address not set");

        // 准备初始化数据
        bytes memory initData = abi.encodeCall(
            StockToken.initialize,
            (name, symbol, initialSupply, msg.sender, oracleAggregator, usdtTokenAddress)
        );

        // 使用代理模式创建新的 StockToken 实例
        ERC1967Proxy proxy = new ERC1967Proxy(stockTokenImplementation, initData);
        address tokenAddress = address(proxy);

        tokenBySymbol[symbol] = tokenAddress;
        allTokens.push(tokenAddress);

        emit TokenCreated(tokenAddress, name, symbol);
        return tokenAddress;
    }

    // 获取代币地址
    function getTokenAddress(string memory symbol) external view returns (address) {
        return tokenBySymbol[symbol];
    }

    // 获取所有代币
    function getAllTokens() external view returns (address[] memory) {
        return allTokens;
    }

    // 更新预言机地址
    function setOracleAggregator(address newOracle) external onlyOwner {
        require(newOracle != address(0), "Invalid oracle address");
        oracleAggregator = newOracle;
        emit OracleUpdated(newOracle);
    }

    // 设置 StockToken 实现合约地址
    function setStockTokenImplementation(address newImplementation) external onlyOwner {
        require(newImplementation != address(0), "Invalid implementation address");
        stockTokenImplementation = newImplementation;
        emit ImplementationUpdated(newImplementation);
    }

    // 设置 USDT 代币地址
    function setUSDTTokenAddress(address newUSDTTokenAddress) external onlyOwner {
        require(newUSDTTokenAddress != address(0), "Invalid USDT token address");
        usdtTokenAddress = newUSDTTokenAddress;
    }
}
